mapper.select:
select 

from sys_enumerate_detailed
where id = #{id,jdbcType=BIGINT}
  and eunm_code = #{eunmCode,jdbcType=VARCHAR}
  and code = #{code,jdbcType=VARCHAR}
  and name = #{name,jdbcType=VARCHAR}

mapper.delete:
delete from sys_enumerate_detailed
where id = #{id,jdbcType=BIGINT}
  and eunm_code = #{eunmCode,jdbcType=VARCHAR}
  and code = #{code,jdbcType=VARCHAR}
  and name = #{name,jdbcType=VARCHAR}

mapper.insert:
insert into sys_enumerate_detailed (id, eunm_code, code, 
  name, validate_flag, create_time, 
  create_user, update_time, update_user
  )
values (#{id,jdbcType=BIGINT}, #{eunmCode,jdbcType=VARCHAR}, #{code,jdbcType=VARCHAR}, 
  #{name,jdbcType=VARCHAR}, #{validateFlag,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{createUser,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{updateUser,jdbcType=VARCHAR}
  )

mapper.update:
update sys_enumerate_detailed


    validate_flag = #{validateFlag,jdbcType=VARCHAR},


    create_time = #{createTime,jdbcType=TIMESTAMP},


    create_user = #{createUser,jdbcType=VARCHAR},


    update_time = #{updateTime,jdbcType=TIMESTAMP},


    update_user = #{updateUser,jdbcType=VARCHAR},


where id = #{id,jdbcType=BIGINT}
  and eunm_code = #{eunmCode,jdbcType=VARCHAR}
  and code = #{code,jdbcType=VARCHAR}
  and name = #{name,jdbcType=VARCHAR}

update sys_enumerate_detailed
set validate_flag = #{validateFlag,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  create_user = #{createUser,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP},
  update_user = #{updateUser,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}
  and eunm_code = #{eunmCode,jdbcType=VARCHAR}
  and code = #{code,jdbcType=VARCHAR}
  and name = #{name,jdbcType=VARCHAR}
